import { useState } from "react";
import validation from "../validation";
import style from '../About/About.module.css'


export default function Form({login}) {
    const [userData,setUserData] = useState({
        email:'',
        password:'',
    });  
    const [errors,setErrors] = useState({});
    
    const handleChange = (event) => {
        setUserData({...userData,[event.target.name]: event.target.value});
        setErrors(validation({...userData,[event.target.name]: event.target.value}));
    };
    const handleSubmit = (event) => {
        event.preventDefault()
        login(userData);
    }
    return (
        <form onSubmit={handleSubmit}>
            <div >
            
            <label>Email</label>
            <input name="email" type="email" placeholder="Digite seu e-mail" value={userData.email} onChange={handleChange}/>
            {errors.email && <p>{errors.email}</p>}
            
            </div>

            <label htmlFor="">Password</label>
            <input name="password" type="password" placeholder="coloque sua senha" value={userData.password} onChange={handleChange}/>
            {errors.password && <p>{errors.password}</p>}
            <button type="submit" >Submit</button>
        </form>
    );
}
